#
# Structure for table "jtbc_dongtaibobao"
#

DROP TABLE IF EXISTS `jtbc_dongtaibobao`;
CREATE TABLE `jtbc_dongtaibobao` (
  `n_id` int(11) NOT NULL AUTO_INCREMENT,
  `n_topic` varchar(200) DEFAULT NULL COMMENT '{"autoRequestFormat":"notEmpty","fieldType":"text"}',
  `n_content` text COMMENT '{"fieldType":"editor"}',
  `n_att` text COMMENT '{"fieldType":"att","fieldRelatedEditor":"content","uploadStatusAutoUpdate":"true"}',
  `n_publish` int(11) DEFAULT '0' COMMENT '{"fieldType":"publish"}',
  `n_time` datetime DEFAULT NULL COMMENT '{"fieldType":"text","fieldHideMode":"0"}',
  `n_lang` int(11) DEFAULT '0',
  `n_delete` int(11) DEFAULT '0',
  PRIMARY KEY (`n_id`),
  KEY `n_delete` (`n_delete`,`n_lang`,`n_publish`) USING BTREE,
  KEY `n_time` (`n_time`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

#
# Data for table "jtbc_dongtaibobao"
#

INSERT INTO `jtbc_dongtaibobao` VALUES (1,'中国出版集团公司与淮安市人民政府签署战略合作协议','<p>7月14日，中国出版集团公司与淮安市人民政府在淮安市政府会议中心签署了战略合作协议。双方就共同传播中华传统诗词，共同实施&ldquo;中华优秀传统文化新媒体工程&rdquo;、&ldquo;中华优秀传统文化国民素养提高工程&rdquo;，共同打造国学书店，共同举办&ldquo;纪念周恩来诞辰120周年全球华语诗词大赛&rdquo;，共同打造文化教育类图书出版发行基地等事宜达成合作。</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;淮安市代市长蔡丽新出席并致辞，中国出版集团副总裁潘凯雄与淮安市副市长王红红分别代表合作双方签署了协议。中国出版传媒股份有限公司出版部主任刘祚臣、科技与数字出版部主任赖雪梅、中版文化传播总经理包岩、副总经理沈昊，以及淮安市委宣传部、市发改委、财政局、教育局、统计局、里运河办、西游记集团等部门负责人出席了仪式。淮安市文广新局局长、党委书记张冲林主持了签约仪式。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"http://www.chinstyle.com/zbwh/132821/143050/%E7%AD%BE%E7%BA%A6%E7%8E%B0%E5%9C%BA.jpg\" /></p>\r\n\r\n<p>签约现场</p>\r\n\r\n<p><img src=\"http://www.chinstyle.com/zbwh/132821/143050/%E6%BD%98%E5%87%AF%E9%9B%84%E5%89%AF%E6%80%BB%E8%A3%81%E8%AE%B2%E8%AF%9D.jpg\" /></p>\r\n\r\n<p>潘凯雄副总裁讲话</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"http://www.chinstyle.com/zbwh/132821/143050/%E8%94%A1%E4%B8%BD%E6%96%B0%E4%BB%A3%E5%B8%82%E9%95%BF%E8%AE%B2%E8%AF%9D.jpg\" /></p>\r\n\r\n<p>蔡丽新代市长讲话</p>\r\n\r\n<p><br />\r\n<strong>&ldquo;诗词中国&rdquo;入驻淮安，&ldquo;诗词中国博物馆&rdquo;正在筹备</strong></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此次合作，中国出版集团旗下最知名的传统文化普及推广品牌&ldquo;诗词中国&rdquo;将入驻淮安，设立&ldquo;诗词中国博物馆&rdquo;，面向淮安市中小学生和广大市民全方位推广中国传统诗词。据悉，诗词中国博物馆将设置&ldquo;诗词中国青少年互动体验中心&rdquo;、&ldquo;诗词中国Chinstyle文创产品工作坊&rdquo;、&ldquo;诗词中国创作基地&rdquo;等互动场地。在14日的活动上，淮安市文化广电新闻出版局局长、党委书记张冲林与中版文化传播总经理包岩共同为&ldquo;诗词中国博物馆筹备处&rdquo;揭牌。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"http://www.chinstyle.com/zbwh/resource/cms/2017/07/%E5%8D%9A%E7%89%A9%E9%A6%86%E6%8F%AD%E7%89%8C2(1).jpg\" /></p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>纪念周恩来系列活动启动，为中华之崛起而读书</strong></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;为中华之崛起而读书&mdash;&mdash;揭幕仪式上，随着人民网总编辑余清楚先生题写的这幅书法作品被徐徐展开，由双方携手打造的&ldquo;为中华之崛起而读书&rdquo;全球华语诗词大赛正式启动。</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;淮安是伟人周恩来的故乡，也是一座具有深厚文化底蕴和丰富文化资源的城市。此次合作，中国出版集团公司携手人民网及淮安市政府，围绕&ldquo;纪念周恩来诞辰120周年&rdquo;这一主题，开展了一系列的文化活动。其中，全球华语诗词大赛将由一手打造了全国最大规模线上诗词大赛的中国出版集团&ldquo;诗词中国&rdquo;团队操刀负责，有望继&ldquo;诗词中国&rdquo;之后，再现一个&ldquo;高规格、大规模、全媒体&rdquo;的诗词盛会。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"http://www.chinstyle.com/zbwh/132821/143050/%E5%A4%A7%E8%B5%9B%E4%B8%BB%E9%A2%98%E5%8F%91%E5%B8%83.jpg\" /></p>\r\n\r\n<p>人民网总编辑余清楚（左三）、中国出版传媒股份有限公司科技与数字出版部主任赖雪梅（右三）、</p>\r\n\r\n<p>中版文化传播副总经理沈昊（右二）等与淮安市相关部门负责人共同发布大赛主题</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>国学直播+知识竞赛，两大&ldquo;工程&rdquo;打造国学大赛</strong></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp;&nbsp; 随着《中国诗词大会》、《诗书中华》等节目的播出，社会上掀起了一股中华优秀传统文化的复兴热潮，国学教育也成为了人们普遍关注的话题。<br />\r\n&nbsp;&nbsp;&nbsp; 此次中国出版集团公司与淮安市政府的合作中明确提出，双方将围绕国学普及与传播，共同实施&ldquo;中华优秀传统文化新媒体工程&rdquo;和&ldquo;中华优秀传统文化国民素养提高工程&rdquo;。这两项工程包含了在淮安市小学、初高中范围内实现中国优秀传统文化课的直播互动教学，以及以新媒体方式分享一线城市、一流大学、示范中学、一流教师的优秀国学教学资源，并组织中小学生参加由中国出版集团公司举办的&ldquo;国学考级&rdquo;及&ldquo;中华之星&rdquo;传统文化常识知识培训及学科竞赛等。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"http://www.chinstyle.com/zbwh/resource/cms/2017/07/%E4%B8%AD%E5%BF%83%E6%8F%AD%E7%89%8C2(1).jpg\" /></p>\r\n\r\n<p>中国出版传媒股份有限公司出版部主任刘祚臣（右）与唐晓军先生为&ldquo;中版集团文教图书印制发行中心&rdquo;揭牌</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; 此外，该战略合作协议中还包括在淮安共同建设&ldquo;最美国学书店&rdquo;、促进国学阅读推广，建立&ldquo;24小时国学图书自助驿站&rdquo;，建立教材教辅出版发行基地，建立数字出版基地等事宜。&ldquo;中国出版集团是出版业的&ldquo;国家队&rdquo;，集聚了中国出版业一批最具品牌影响力、文化积累非常厚重的出版单位。淮安是历史文化名城，也是&lsquo;诗词之乡&rsquo;，人杰地灵。此次，双方携手打造城市文化战略，相信一定能有光明的前景，同时预祝合作圆满成功。&rdquo;</p>\r\n','',1,'2018-03-02 00:52:56',0,0),(2,'【视频】“诗意中国”中秋原创诗会','<p><strong>第三届&ldquo;诗词中国&rdquo;投稿突破22万<br />\r\n联手世纪坛举办&ldquo;诗意中国&rdquo;原创诗会</strong></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 由中华书局发起，联合中国出版集团、中央电视台、光明日报、中华诗词研究院、中华诗词学会、人民网、中国移动共同举办的第三届&ldquo;诗词中国&rdquo;传统诗词创作大赛，已于2016年11月30日结束征稿。组委会日前发布消息，大赛自开赛至今，征集到的原创诗词作品已突破22万首，超过前两届之和。</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&ldquo;第三届诗词中国在延续了充分运用新媒体工具的同时，增加了对落地活动的建设，让&lsquo;诗词中国&rsquo;的品牌更加亲民，这也是投稿量大幅增长的一个重要原因。&rdquo;组委会工作人员表示。2016年6月18日，第三届&ldquo;诗词中国&rdquo;大赛暨影响力诗人评选活动在人民日报新媒体大楼一号演播厅举办；9月14日，大赛组委会与中华世纪坛联手举办的&ldquo;诗意中国&rdquo;中秋原创诗会在世纪坛南广场精彩上演，来自全国各地的诗人代表，首都军人、学生、聋哑儿童、诗词爱好者、企业代表和美好家庭代表，中华世纪坛会员及合作教育机构代表等共700余人欢聚一堂，共迎中秋佳节。</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 诗会以&ldquo;月亮&rdquo;为线索，分为把酒共赏月、望月怀故乡、邀月共团圆、天涯共婵娟四个篇章，分别从赏月、怀乡、团圆、寄情四个层次传递中秋意蕴。以诗歌为核心，采用朗诵、演唱、舞蹈、演奏、情景表演、杂技等多种表演形式多维度呈现。著名演播艺术家雅坤，著名老艺术家谢芳、张目，央视节目主持人朱迅，京剧表演艺术家储兰兰，青年演员蒋小涵、孙茜等作为表演嘉宾助阵诗会。作为第三届&ldquo;诗词中国&rdquo;的全程合作伙伴，碧桂园&bull;森林城市也对本次诗会给予了大力支持。</p>\r\n','',1,'2018-03-02 00:53:06',0,0),(3,'“诗词中国”传统诗词大赛鸣锣','<p>本报讯（实习生 于芳溪）第三届&ldquo;诗词中国&rdquo;传统诗词创作大赛日前在京启动，&ldquo;互联网+诗词&rdquo;成为本届大赛的鲜明特色。</p>\r\n\r\n<p>　　本届大赛将通过全媒体进行传播，在延续前两届活动的基础上成立了诗词中国云科技联盟，新增咪咕诗词咖秀大赛，将时下流行的原创音视频及动漫元素与传统诗词相融合。启动仪式现场发布了&ldquo;诗词中国&rdquo;公众影响力诗人、网络影响力诗人、新诗影响力诗人和卓越影响力诗人，刘征、欧阳鹤、蔡厚示等文化名家，以及余秀华、伊沙等当代优秀诗人在现场接受了这份荣誉。最后，组委会设计了&ldquo;诗词中国&rdquo;主题研讨会环节，嘉宾就&ldquo;中国传统诗词在新媒体环境下的发展与传播&rdquo;&ldquo;一脉骚香传域外&mdash;&mdash;中日名家谈海外汉诗&rdquo;&ldquo;中国传统诗词文化继承与发扬&rdquo;三个主题展开讨论。</p>\r\n\r\n<p>　　第三届&ldquo;诗词中国&rdquo;传统诗词创作大赛自2016年6月18日开始征稿，用户可通过官网、微信等多种渠道上传作品进行投稿。作品分绝句、律诗、词、古风四个组别，经过初审、复审、终审三个环节，评选出四个组别的月度及年度创作奖。</p>\r\n','',1,'2018-03-02 00:53:15',0,0),(4,'第三届“诗词中国”7月月度创作奖揭晓，7月复活机制启动~','<p>第三届&ldquo;诗词中国&rdquo;传统诗词创作大赛7月月度创作奖名单经过三天的公示，现正式揭晓，欢迎围观~</p>\r\n\r\n<p>&nbsp;2016年6月18日至7月31日的投稿作品，经过两轮筛选，绝句、律诗、词、古风4个组别共评出月度创作奖拟获奖作品59首。其中：</p>\r\n\r\n<p>&nbsp;绝句29首，律诗12首，词12首，古风6首。同等奖作品排名不分先后。</p>\r\n\r\n<p>详细名单如下：</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n\t<tbody>\r\n\t\t<tr>\r\n\t\t\t<td colspan=\"4\">\r\n\t\t\t<p>第三届&ldquo;诗词中国&rdquo;传统诗词创作大赛&nbsp;&nbsp; 7月份月度创作奖名单&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\t\t\t</td>\r\n\t\t\t<td colspan=\"1\">&nbsp;</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td colspan=\"5\">\r\n\t\t\t<p>绝句组&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\t\t\t</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td colspan=\"5\">\r\n\t\t\t<p>一等奖4名，二等奖10名，三等奖15名。总计29名。</p>\r\n\t\t\t</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td>\r\n\t\t\t<p>作品ID&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>标题&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>手机号码&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>奖项&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\t\t\t</td>\r\n\t\t\t<td>&nbsp;</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td>\r\n\t\t\t<p>101295</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>喜雨</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>158****3578</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>一等奖</p>\r\n\t\t\t</td>\r\n\t\t\t<td>&nbsp;</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td>\r\n\t\t\t<p>112783</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>咏芭蕉</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>189****4514</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>一等奖</p>\r\n\t\t\t</td>\r\n\t\t\t<td>&nbsp;</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td>\r\n\t\t\t<p>166105</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>大暑</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>138****6005</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>一等奖</p>\r\n\t\t\t</td>\r\n\t\t\t<td>&nbsp;</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td>\r\n\t\t\t<p>166062</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>雨旅途记</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>185****2957</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>一等奖</p>\r\n\t\t\t</td>\r\n\t\t\t<td>&nbsp;</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td>\r\n\t\t\t<p>132589</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>七七感怀</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>188****2171</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>二等奖</p>\r\n\t\t\t</td>\r\n\t\t\t<td>&nbsp;</td>\r\n\t\t</tr>\r\n\t\t<tr>\r\n\t\t\t<td>\r\n\t\t\t<p>154842</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>题帝喾祠</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>182****5872</p>\r\n\t\t\t</td>\r\n\t\t\t<td>\r\n\t\t\t<p>二等奖</p>\r\n\t\t\t</td>\r\n\t\t\t<td>&nbsp;<